export const searching = (value) => {
  axios
    .get(
      `${basicURL}${searchType}?page=${value}&query=${searchTerm}&api_key=${tmdbKey}`
    )
    .then((res) => res.data)
    .then((result) => {
      saveSearch(result);
      setActiveSearch(true);
    });
};
