import axios from "axios";
import React, { useContext, useState } from "react";
import { AuthContext } from "../context/authContex";


const Card = function ({item}) {
  //const[faved,setFaved]=useState(false)
  const faved = localStorage.getItem('faved') || false 
  const {loggedUser}=useContext(AuthContext)
  const addtoFavorite= ()=>{
    const name= item.original_title|| item.original_name
    const filmId=item.id
    const mediaType= item.first_air_date? 'tv':'movie'
    const userId=loggedUser.id
   axios.post('/favorites',{filmId:filmId, Name:name, mediaType: mediaType, userId:userId})

  }



  return (
    <>
      <h2>{item.original_title}</h2>
      {item.poster_path ? (
        <img
          src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}
          alt={"poster path"}
        />
      ) : (
        <img
          src={"https://www.regalrexnord.com/assets/media/placeholder.jpg"}
          alt={"poster path"}
        />
      )}

      <details>
        {item.release_date && <p>{`Release year: ${item.release_date.split('-').reverse().join('-')}`}</p>}
        {item.first_air_date && <p>{`First air date: ${item.first_air_date.split('-').reverse().join('-')}`}</p>}
        <p>{`Overview: ${item.overview}`}</p>
        <p>{`Score: ${item.vote_average}(${item.vote_count})votes)`}</p>
      </details>


      {!loggedUser.id && <p>log in to add to favorites</p>}
      {!faved? <button onClick={addtoFavorite} >Add to favorites</button>:<button onClick={removeFavorite}>Remove favorite</button>}
    </>
  );
};

export default Card;
