  if (!savedSearch.length) {return <h3>Waiting... </h3>}
// if (savedSearch.length===0){ console.log('LOADING')}


  return <div>
     <h1>CONTENT</h1>
     <ul>
     {savedSearch.map((item,i)=>{return (<Card/>)})}

     </ul>
    {/* <h2>{savedSearch[0].original_title}</h2> */}

  </div>
};

export default Content
