import {React , createContext} from "react"

const initialState = {
user : null ,
isAuthenticated : false,
toggleAuth : () => null
}

export const AuthContext = createContext(initialState)




const AuthContextProvider = ({children}) => {
const [isLoggedIn, setIsLoggedIn] = useState(()=>{
  const logUser = localStorage.getItem('logUser') || null
  const logAuth = localStorage.getItem('logAuth') || false 
  return {
    user : logUser,
    isAuthenticated: logAuth, 
  }
  })

const toggleAuth = (user) =>
setIsLoggedIn({
  user: user,
  isAuthenticated: !isLoggedIn.isAuthenticated,
},
localStorage.setItem('logUser', isLoggedIn.user), 
localStorage.setItem('logAuth', isLoggedIn.isAuthenticated));
console.log('user', isLoggedIn.user);  
return <AuthContext.Provider value = {{...isLoggedIn , toggleAuth }}>{children}</AuthContext.Provider>}
//los 3 puntos son para redefinir el objeto


export default AuthContextProvider
