  //   searching(current+1)}

  return (<div className="pagination">
    {/* {current!==1 && <button onClick={()=>previousPage(current)}> prev</button>} */}
    {pages}
    {/* {current < pages.length &&<button onClick={()=>nextPage(current)}>next</button>} */}
    </div>)
};

export default Pages;
