import axios from "axios";
import React,{ useContext }  from "react";
import { SearchContext } from "../context/searchContext";
import Card from "./Card"




const Content = function () {
  const {savedSearch, saveSearch, tmdbKey, previousSearch}=useContext (SearchContext)


 if (!savedSearch.results) {axios.get(`https://api.themoviedb.org/3/trending/movie/week?api_key=${tmdbKey}`)
 .then((res) => res.data)
.then((result) => saveSearch(result))}
console.log(savedSearch)


if (savedSearch.results){
  return (<div>
    {previousSearch? <h1>SEARCH RESULT</h1>: <h1>TRENDING</h1>}
     <ul>
     {savedSearch.results.map((item,i)=>{
       return (<Card data={item} key={item.id}/>)})}
     </ul>
  </div>)}
  else {return <p>LOADING</p>}
};

export default Content
