import axios from "axios";
import React, { useState, useContext } from "react";
import { SearchContext } from "../context/searchContext";

 const Search = function () {


  const {savedSearch, saveSearch,searching,searchTerm, setSearchTerm,searchType,setSearchType,pageNum,setPageNum}=useContext (SearchContext)


  const handleSearchTerm = function (e) {
    return setSearchTerm(e.target.value);
  };
  const handleSearchType= function(value){
    return setSearchType(value)
  }
  const handleSubmit = function (e) {
    e.preventDefault();
    const encodedSearch = encodeURI(searchTerm);
    if(searchType=== "") return <p>SELECT TYPE</p>
    axios
      .get(`${basicURL}${searchType}?query=${encodedSearch}&api_key=${tmdbKey}`)
      .then((res) => res.data)
     .then((result) => saveSearch(result));

  };
console.log(savedSearch)
console.log(searchType)

  return (
    <>
    <form onSubmit={handleSubmit}>
      <input type={"text"} placeholder="search" onChange={handleSearchTerm}></input>
      <button type={'button'} onClick={()=>handleSearchType('movie')} > Movies</button>
      <button type={'button'} onClick={()=>handleSearchType('tv')} > TV</button>
      <button> SUBMIT</button>
    </form>
    </>
  );
};

export default Search
