import { createContext, useState } from "react";

const initialState={
    savedSearch:[],
    saveSearch:()=>{},
    tmdbKey : "5f4cf67aa6b81c56e16cff977537cf17",
    basicURL : "https://api.themoviedb.org/3/search/",
    searchTerm:'',
    searchType:'',
}

export const SearchContext= createContext(initialState);

const SearchContextProvider=({children})=>{
 const [savedSearch,setSavedSearch]=useState({
     savedSearch:[]
 })
 const [searchTerm, setSearchTerm]=useState({
     searchTerm:''
 })
 const [searchType,setSearchType]=useState({
    searchType:''
})

 const saveSearch=(searchResult)=>{
     setSavedSearch({savedSearch:searchResult})   
 }
return <SearchContext.Provider value={{...savedSearch, saveSearch}}>{children}</SearchContext.Provider>
}
; 
export default SearchContextProvider

const tmdbKey = "5f4cf67aa6b81c56e16cff977537cf17";
const basicURL = "https://api.themoviedb.org/3/search/";




const handleSearchTerm = function (e) {
  return setSearchTerm(encodeURI(e.target.value));
};
const handleSearchType= function(value){
  return setSearchType(value)
}
const handleSubmit = function (e) {
  e.preventDefault();

  if(searchType=== "") return <p>SELECT TYPE</p>
  axios
    .get(`${basicURL}${searchType}?page${pageNum}&query=${searchTerm}&api_key=${tmdbKey}`)
    .then((res) => res.data)
   .then((result) => saveSearch(result));

};
