import React from "react";

const Card = function (data, key) {
  const item = data.item;
  console.log(item.overview);
  return (
    <>
      <h2>{item.original_title}</h2>
      <img src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`} />
      <p>{item.overview}</p>
    </>
  );
};

export default Card;
