import React, { useContext, useState } from "react";
import { SearchContext } from "../context/searchContext";

const Pages = () => {
  const { savedSearch, searching } = useContext(SearchContext);
  const [current,setCurrent]=useState(1)
  const numberOfPages = savedSearch.total_pages;
  const pages = [];
  const handlePageChange = (i) => {
    setCurrent(i)
    searching(i);
  };

  for (let i = 1; i <= numberOfPages; i++) {
    pages.push(
      <button className="pagination-btn"
        style={{ display: "inline", padding: "10px", color: "blue" }}
        key={i}
        onClick={() => handlePageChange(i)}
      >
        {i}
      </button>
    );
  }
  console.log('SOY CURRENT', current );
  console.log('SOY PAGES', pages );

  const previousPage=(current)=>{
    if(current>1){
    setCurrent(current-1)
    searching(current-1)}

  }

  return (<div className="pagination">
    {current!==1 && <button onClick={()=>previousPage(current)}> prev</button>}
    {pages}
    {current < pages.length &&<button onClick={()=>previousPage}>next</button>}
    </div>)
};

export default Pages;
