import React, { useContext } from "react";
import { AuthContext } from "../context/authContex";


const Card = function (data, key) {
  const item = data.item;
  const loggedUser=useContext(AuthContext)
  return (
    <>
      <h2>{item.original_title}</h2>
      {item.poster_path ? (
        <img
          src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}
          alt={"poster path"}
        />
      ) : (
        <img
          src={"https://www.regalrexnord.com/assets/media/placeholder.jpg"}
          alt={"poster path"}
        />
      )}
      <p>{item.overview}</p>
      {loggedUser.id?<button>Add to favorites</button>:<p>log in to add to favorites</p>}
    </>
  );
};

export default Card;
