    <p>{`My Email is: ${loggedUser.email} `}</p>
       { myFavs?  
     <ul>
         {myFavs.map((item,i)=>{
             return(<li key={i} id={myFavs.id}>{item.Name}<button onClick={()=>removeFavorite(item.id)}>remove</button></li>)
         })}
