import { createContext, useState } from "react";

const initialState={
    savedSearch:[],
    saveSearch:()=>{},
    searching:()=>{},
    searchTerm:'',
    searchType:'',
    pageNum:''
}

export const SearchContext= createContext(initialState);

const SearchContextProvider=({children})=>{
 const [savedSearch,setSavedSearch]=useState({
     savedSearch:[]
 })
 const [searchTerm, setSearchTerm]=useState({
     searchTerm:''
 })
 const [searchType,setSearchType]=useState({
    searchType:''
})
const [pageNum, setPageNum]=useState({
pageNum:'1'
})

 const saveSearch=(searchResult)=>{
     setSavedSearch({savedSearch:searchResult})   
 }

 const searching=()=>{
     axios
    .get(`https://api.themoviedb.org/3/search/${searchType}?page${pageNum}&query=${searchTerm}&api_key=5f4cf67aa6b81c56e16cff977537cf17`)
    .then((res) => res.data)
   .then((result) => saveSearch(result));}


return <SearchContext.Provider value={{...savedSearch, saveSearch,searching,}}>{children}</SearchContext.Provider>
}
; 
export default SearchContextProvider


const handleSearchTerm = function (e) {
  return setSearchTerm(encodeURI(e.target.value));
};
const handleSearchType= function(value){
  return setSearchType(value)
}
const handleSubmit = function (e) {
  e.preventDefault();

  if(searchType=== "") return <p>SELECT TYPE</p>


};
