import axios from "axios";
import React,{ useContext, useEffect,  }  from "react";
import { SearchContext } from "../context/searchContext";
import Card from "./Card"




const Content = function () {
  const {savedSearch, saveSearch, tmdbKey}=useContext (SearchContext)


 // if (!savedSearch.results) return <p>HELLO</p>




 if (!savedSearch.results) {axios.get(`https://api.themoviedb.org/3/trending/movie/week?api_key=${tmdbKey}`)
 .then((res) => res.data)
.then((result) => saveSearch(result)).then(()=> {return (<div>
  <h1>CONTENT</h1>
  <ul>
  {savedSearch.results.map((item,i)=>{
    return (<Card item={item} key={i}/>)})}
  </ul>
</div>)})};




};

export default Content
