import axios from "axios";
import React from "react";
import { useNavigate } from "react-router-dom";
import { useInput } from "../utils/customHooks";


const Register=()=>{
    const navigate=useNavigate()
    const email = useInput("email");
    const password = useInput("password");
    const handleSubmit=(e)=>{
        e.preventDefault();
        axios.post("/register", {
            email: email.value,
            password: password.value,
          }).then(alert('USER CREATED SUCCESFULLY')).then(()=>navigate('/'))
    }

    return(
        <>
        <p>CREA TU USUARIO</p>
        <form onSubmit={handleSubmit}>           
        <input type='text' placeholder="ingrese su email" {...email}></input>
        <input type='password' placeholder="ingrese su password"{...password}></input>
        <button>SUBMIT</button>
        </form>
        </>
    )
}

export default Register
