    setSearchTerm(e.target.value);
  };

  const handleSubmit = function (e) {
    e.preventDefault();

    const encodedSearch = encodeURI(searchTerm);
    console.log("SOY ENCODED", encodedSearch);
    console.log(`${basicURL + encodedSearch}&api_key=${tmdbKey}`);
  };
