import { createContext, useState } from "react";
import axios from "axios";

const initialState={
    savedSearch:[],
    saveSearch:()=>{},
    searching:()=>{},
    searchTerm:'',
    searchType:'',
    tmdbKey : "5f4cf67aa6b81c56e16cff977537cf17",
    basicURL : "https://api.themoviedb.org/3/search/",
}

export const SearchContext= createContext(initialState);

const SearchContextProvider=({children})=>{
 const [savedSearch,setSavedSearch]=useState({
     savedSearch:[]
 })
 const [searchTerm, setSearchTerm]=useState({
     searchTerm:''
 })
 const [searchType,setSearchType]=useState({
    searchType:''
})

 const saveSearch=(searchResult)=>{
     setSavedSearch({savedSearch:searchResult})   
 }

 const searching=(value)=>{
     axios
    .get(`${basicURL}${searchType}?page=${value}&query=${searchTerm}&api_key=${tmdbKey}`)
    .then((res) => res.data)
   .then((result) => saveSearch(result));}


return <SearchContext.Provider value={{...savedSearch, saveSearch,searching,searchTerm, setSearchTerm,searchType,setSearchType,pageNum,setPageNum}}>{children}</SearchContext.Provider>
}
; 
export default SearchContextProvider
