import React, {  useContext, useState } from "react";
import { SearchContext } from "../context/searchContext";

 const Search = function () {

  const {searching, setSearchTerm,searchType,setSearchType, setActiveSearch}=useContext (SearchContext)
  const [selected,setSelected]=useState(false)

  const handleSearchTerm = function (e) {
    return setSearchTerm(encodeURI(e.target.value));
  };
  const handleSearchType= function(value){
    setSelected(!selected)
    return setSearchType(value)
  }
  const handleSubmit = function (e) {
    e.preventDefault();
    if(typeof searchType === 'object') return alert("SELECT TYPE OF SEARCH")
    setActiveSearch(true)
    searching()

  };

  return (
    <div style={{paddingTop:'30px'}}>
    <form onSubmit={handleSubmit}>

      <input className='searchBar' type={"text"} placeholder="search" onChange={handleSearchTerm}></input>
      <p>Please select type of content before search</p>
      <button className='button-searchType' type={'button'} onClick={()=>handleSearchType('movie')} > Movies</button>
      <button className='button-searchType' type={'button'} onClick={()=>handleSearchType('tv')} > TV</button>
      <button className='button'> GO!</button>
    </form>
    </div>
  );
};

export default Search
