import React, {  useContext } from "react";
import { SearchContext } from "../context/searchContext";

 const Search = function () {

  const {searching, setSearchTerm,searchType,setSearchType, setActiveSearch}=useContext (SearchContext)


  const handleSearchTerm = function (e) {
    return setSearchTerm(encodeURI(e.target.value));
  };
  const handleSearchType= function(value){
    return setSearchType(value)
  }
  const handleSubmit = function (e) {
    e.preventDefault();
    if(typeof searchType === 'object') return alert("SELECT TYPE OF SEARCH")
    setActiveSearch(true)
    searching()

  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <input type={"text"} placeholder="search" onChange={handleSearchTerm}></input>
      <button className='button' type={'button'} onClick={()=>handleSearchType('movie')} > Movies</button>
      <button type={'button'} onClick={()=>handleSearchType('tv')} > TV</button>
      <button> SUBMIT</button>
    </form>
    </>
  );
};

export default Search
