import axios from "axios";
import React, { useState, useContext } from "react";
import { SearchContext } from "../context/searchContext";

 const Search = function () {
  const tmdbKey = "5f4cf67aa6b81c56e16cff977537cf17";
  const basicURL = "https://api.themoviedb.org/3/search/movie?query=";
  const [searchTerm, setSearchTerm] = useState("");
  const {savedSearch, saveSearch}=useContext (SearchContext)

  const handleSearchTerm = function (e) {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = function (e) {
    e.preventDefault();
    const encodedSearch = encodeURI(searchTerm);

    axios
      .get(`${basicURL + encodedSearch}&api_key=${tmdbKey}`)
      .then((res) => res.data)
     .then((result) => saveSearch(result));

  };

  return (
    <>
    <form onSubmit={handleSubmit}>
      <input type={"text"} placeholder="search" onChange={handleSearchTerm}></input>
      <button onClick> Movies</button>
      <button onClick> TV</button>
      <button> SUBMIT</button>
    </form>
    </>
  );
};

export default Search
