import axios from "axios";
import React, {useContext}from "react";

import { useInput } from "../utils/customHooks";
import { AuthContext } from "../context/authContex";
import { useNavigate } from "react-router-dom";

const Login=()=>{
    const navigate=useNavigate()
    const {loggedUser, setLoggedUser}= useContext(AuthContext)

    const email= useInput('email')
    const password=useInput('password')

    const handleSubmit=(e)=>{
       e.preventDefault();
       axios.post('/login',{ 
        email: email.value,
        password: password.value,}).then((user)=>setLoggedUser(user.data)).then(()=>alert('WELCOME')).then(()=>navigate('/'))
    }

    console.log(loggedUser)

    return(
        <div>
        <p>Log</p>
        <form onSubmit={handleSubmit}>
        <input type='text' placeholder="ingrese su email" {...email}></input>
        <input type='text' placeholder="ingrese su password" {...password}></input>
        <button>SUBMIT</button>
        </form>
        </div>
    )
}

export default Login
