        axios.get('/friends',{email:friendEmail.value}).then((res)=>{console.log(res)})

    }

 return (

         <form className="user-form" style={{marginTop:'-10%'}} onSubmit={searchFriend}>
             <input className="searchBar" type="text" placeholder='enter email' {...friendEmail} ></input>
             <button className="button" >Find friends!</button>
         </form>

 )
}

