        axios
          .get("/me")
          .then((res) => {console.log('SOY RES',res.data); return res.data})
          .then((user) => {
            saveUser(user)

          });
      },[savedSearch]);


    return(
    <div>
        <Search/>
        <Content/>


    </div>)
}

export default Body
