import axios from "axios";
import React, {useContext, useState}from "react";
import { useInput } from "../utils/customHooks";
import { AuthContext } from "../context/authContex";

const Login=()=>{
    const [papa,setPapa]=useState({})
    const email= useInput('email')
    const password=useInput('password')
    const {loggedUser, setloggedUser}=useContext(AuthContext)
    const handleSubmit=(e)=>{
       e.preventDefault();
       axios.post('/login',{ 
        email: email.value,
        password: password.value,}).then((user)=>setloggedUser(user))
    }

console.log('SOY LOGGED USER', loggedUser)
    return(
        <div>
        <p>Log</p>
        <form onSubmit={handleSubmit}>
        <input type='text' placeholder="ingrese su email" {...email}></input>
        <input type='text' placeholder="ingrese su password" {...password}></input>
        <button>SUBMIT</button>
        </form>
        </div>
    )
}

export default Login
