    const {loggedUser,saveUser}=useContext(AuthContext)
    const [myFavs, setMyFavs]=useState('')
    const findFavorites=()=>{axios.get(`/favorites/${userId}`).then((res)=> res.data).then((res)=>setMyFavs(res))}
    const userId=loggedUser.id
    //const info=JSON.parse(myFavs)
    // console.log(info)



    useEffect(() => {
        axios
          .get("/me")
          .then((res) => res.data)
          .then((user) => {
            saveUser(user)
          });

