      }

    return(
    <div>
      <h1>MY INFO</h1>
      <p>{`My User ID is: ${loggedUser.id} `}</p>
      <p>{`My Email is: ${loggedUser.email} `}</p>
       { myFavs?  
          <ul>
            {myFavs.map((item)=>{const data=JSON.parse(item.jsonData)
              return (<Card item={data} key= {item.id} id={item.id}/>)})}
          </ul>
              : 
          <p>LOADING</p>}
    </div>)






}







export default User
