import axios from "axios";
import React, { useState, useContext } from "react";
import { SearchContext } from "../context/searchContext";

 const Search = function () {
  const tmdbKey = "5f4cf67aa6b81c56e16cff977537cf17";
  const basicURL = "https://api.themoviedb.org/3/search/";
  const [searchTerm, setSearchTerm] = useState("");
  const [searchType, setSearchType]=useState('')
  const {savedSearch, saveSearch}=useContext (SearchContext)

  const handleType=function(value){
    setSearchType(value)

  }

  const handleSearch = function (e) {
    setSearchTerm(e.target.value);
  };

  const handleSubmit = function (e) {
    e.preventDefault();
    const encodedSearch = encodeURI(searchTerm);

    axios
      .get(`${basicURL + encodedSearch}&api_key=${tmdbKey}`)
      .then((res) => res.data.results)
     .then((result) => saveSearch(result));

  };


  return (
    <form onSubmit={handleSubmit}> 
    <input type={"text"} placeholder="search" onChange={handleSearch}></input>
    <input type="radio" value='tv' onClick={()=>{handleType('tv')}}/> Series
    <input type="radio" value="movie" onClick={()=>{handleType('movie')}}/> Movies

      <button> SUBMIT</button>
    </form>
  );
};

export default Search
