import axios from "axios";
import React, { useContext } from "react";
import { SearchContext } from "../context/searchContext";

const Pages = () => {
  const { savedSearch, pageNum, setPageNum, searchType, searchTerm } =
    useContext(SearchContext);
  const numberOfPages = savedSearch.total_pages;
  const pages = [];
  const handlePageChange = (i) => {
    setPageNum(i);
    axios
      .get(
        `https://api.themoviedb.org/3/search/${searchType}?page${pageNum}&query=${searchTerm}&api_key=5f4cf67aa6b81c56e16cff977537cf17`
      )
      .then((res) => res.data)
      .then((result) => saveSearch(result));
  };

  console.log(pageNum);
  for (let i = 1; i <= numberOfPages + 1; i++) {
    pages.push(
      <li
        style={{ display: "inline", padding: "10px" }}
        key={i}
        onClick={() => handlePageChange(i)}
      >
        <a href="#">{i}</a>
      </li>
    );
  }

  //   {(for i=1; i<=numberOfPages+1, i++){
  //     <ul>
  //     <li >page 1</li>
  //     <li style={{ display: "inline" }}>page 2</li>
  //     <li style={{ display: "inline" }}>page 3</li>
  //     </ul></>
  //   }}
  return <>{pages}</>;
};

export default Pages;
