import axios from "axios";
import React, { useContext, useState } from "react";
import { AuthContext } from "../context/authContex";


const Card = function ({item}) {
  //console.log(JSON.stringify(item))
  const {loggedUser}=useContext(AuthContext)
  console.log(loggedUser)
  const name= item.original_title|| item.original_name
  const filmId=item.id
  const mediaType= item.first_air_date? 'tv':'movie'
  const userId=loggedUser.id
  const addtoFavorite= ()=>{
   axios.post('/favorites',{filmId:filmId, Name:name, mediaType: mediaType, userId:userId}).then(()=>alert('SUCCESS'))

  }
  return (

    <div className="film" >

      {item.poster_path ? (
        <img className="filmImg"
          src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}
          alt={"poster path"}
        />
        ) : (
        <img className="filmImg"
          src={"https://i.mydramalist.com/1XlL7_4c.jpg?v=1"}
          alt={"poster path"}
        />
      )}

      <div className="itemInfo">
          <h3>{name}</h3>
          <p>{`Rating ${item.vote_average}/10`}</p>
      </div>

      <details>
        {item.release_date && <p>{`Release year: ${item.release_date.split('-').reverse().join('-')}`}</p>}
        {item.first_air_date && <p>{`First air date: ${item.first_air_date.split('-').reverse().join('-')}`}</p>}
        <p style={{overflow:'auto'}}>{`Overview: ${item.overview}`}</p>
      </details>
      {!loggedUser.id? <p>log in to add to favorites</p>:<button onClick={addtoFavorite} >Add to favorites</button>}

    </div>
  );
};

export default Card;
